mapper.select:
		select count(*)
        from MORANMORAN.PRODUCT
        where c_id = #{C_ID}

select pd_id as pId, pi_no as no, pi_img as img
from MORANMORAN.PRODUCT_IMG
where pd_id = #{pd_id}

select count(*)
from (
    SELECT COUNT(*) as hit
    FROM ORDERS
    WHERE pd_id IN (SELECT pd_id FROM PRODUCT)
    GROUP BY pd_id
) as pop;
